import{_ as a}from"./plugin-vue_export-helper-c27b6911.js";import{o as e,c as i,d as h}from"./app-62e25a33.js";const r={},d=h('<h1 id="_1" tabindex="-1"><a class="header-anchor" href="#_1" aria-hidden="true">#</a> 1</h1><h2 id="前言" tabindex="-1"><a class="header-anchor" href="#前言" aria-hidden="true">#</a> 前言</h2><p>房子，是个老生常谈的话题了。大家都想拥有一套自己的房子，因为那该死的归属感和为你而亮的一盏灯。</p><p>打工人，像我这种在深圳的打工人，租一段归属感和一盏亮灯，也是合情合理。</p><p>10月底现在租的房子到期，一室一厅。因为人口扩张，计划着要租就是租两室一厅的。所以也是慢慢开始找房看房。</p><p>看房的过程中，总让我有些错觉，觉得什么都是有公式可以套。</p><h2 id="需求" tabindex="-1"><a class="header-anchor" href="#需求" aria-hidden="true">#</a> 需求</h2><p>类比：软件开发中的需求宣讲</p><p>第一步，是明确自己的找房需求。</p><p>一房？两房？带电梯？空房？精装修？小区绿化？有阳台？能养宠物？</p><p>知道自己想要找什么，才能更加有效率的去筛选房源。以我为例：</p><ul><li>小区房</li><li>两室一厅</li><li>精装 || 空房</li><li>有电梯</li><li>有阳台</li><li>有马桶</li><li>用燃气</li><li>面积不小于40</li><li>离地铁近</li><li>噪音低</li><li>最好能办停车卡</li></ul><p>这一套限制下来，能很大程度过滤一些房源，从而节省我筛选的时间。</p><p>接着</p><h2 id="准备工作" tabindex="-1"><a class="header-anchor" href="#准备工作" aria-hidden="true">#</a> 准备工作</h2><p>这个阶段，其实就是扩大搜素房源的范围，汇总通过各种途径获取到的房源，然后进行筛选，比较</p><h3 id="" tabindex="-1"><a class="header-anchor" href="#" aria-hidden="true">#</a></h3><h3 id="找房app" tabindex="-1"><a class="header-anchor" href="#找房app" aria-hidden="true">#</a> 找房APP</h3><h3 id="朋友圈" tabindex="-1"><a class="header-anchor" href="#朋友圈" aria-hidden="true">#</a> 朋友圈</h3><h3 id="小区工作人员" tabindex="-1"><a class="header-anchor" href="#小区工作人员" aria-hidden="true">#</a> 小区工作人员</h3><h2 id="地图找房" tabindex="-1"><a class="header-anchor" href="#地图找房" aria-hidden="true">#</a> 地图找房</h2><h2 id="中介乱斗" tabindex="-1"><a class="header-anchor" href="#中介乱斗" aria-hidden="true">#</a> 中介乱斗</h2><h2 id="业主博弈" tabindex="-1"><a class="header-anchor" href="#业主博弈" aria-hidden="true">#</a> 业主博弈</h2><h2 id="人生规律" tabindex="-1"><a class="header-anchor" href="#人生规律" aria-hidden="true">#</a> 人生规律</h2>',24),n=[d];function l(t,c){return e(),i("div",null,n)}const o=a(r,[["render",l],["__file","租房二三事.html.vue"]]);export{o as default};
